import { useState } from "react";
import aloeVideo from "../assets/multimedia/aloevera.mp4";
import neemVideo from "../assets/multimedia/neem.mp4";

import ashwagandhaAudio from "../assets/multimedia/ashwagandha.aac";
import fenugreekAudio from "../assets/multimedia/fenugreek.mp3";

const multimedia = {
  images: [
    { title: "Tulsi (Holy Basil)", src: "data:image/jpeg;base64,", source: "Wikimedia Commons" },
    { title: "Neem Leaves", src: "data:image/jpeg;base64,", source: "Wikimedia Commons" },
    { title: "Amla (Indian Gooseberry)", src: "data:image/jpeg;base64,", source: "Wikimedia Commons" },
  ],

  videos: [
    { title: "Aloe Vera  Medicinal Overview", src: aloeVideo, source: "Local Educational Video" },
    { title: "Neem  Uses & Benefits", src: neemVideo, source: "Local Educational Video" },
  ],

  audio: [
    { title: "Ashwagandha  Audio Explanation", src: ashwagandhaAudio, source: "Recorded Explanation", type: "audio/aac" },
    { title: "Fenugreek  Health Benefits", src: fenugreekAudio, source: "Recorded Explanation", type: "audio/mpeg" },
  ],
};

export default function Multimedia() {
  const [tab, setTab] = useState("images");

  return (
    <div className="relative p-10 min-h-screen">
      {/*  Translucent Background Box */}
      <div className="absolute inset-0 bg-white/20 backdrop-blur-xl rounded-3xl m-4 z-0 pointer-events-none" />

      <div className="relative z-10 max-w-6xl mx-auto space-y-8">
        {/* Heading */}
        <h1 className="text-4xl md:text-5xl font-extrabold text-white drop-shadow-lg">
          Multimedia Learning 
        </h1>
        <p className="text-gray-200 max-w-4xl">
          Learn medicinal plants using authentic images, verified educational videos, and spoken articles sourced from trusted public repositories.
        </p>

        {/* Tabs */}
        <div className="flex gap-4 flex-wrap mt-6">
          <TabButton active={tab === "images"} onClick={() => setTab("images")}> Images</TabButton>
          <TabButton active={tab === "videos"} onClick={() => setTab("videos")}> Videos</TabButton>
          <TabButton active={tab === "audio"} onClick={() => setTab("audio")}> Audio</TabButton>
        </div>

        {/* Images */}
        {tab === "images" && (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6">
            {multimedia.images.map((img, i) => (
              <div
                key={i}
                className="bg-white/20 backdrop-blur-md rounded-2xl shadow-lg hover:shadow-2xl hover:scale-105 transition-transform duration-300 overflow-hidden"
              >
                {img.src && (
                  <img
                    src={img.src}
                    alt={img.title}
                    className="h-48 w-full object-cover rounded-t-2xl"
                  />
                )}
                <div className="p-4 space-y-1">
                  <h3 className="font-semibold text-white text-lg">{img.title}</h3>
                  <p className="text-gray-300 text-xs">Source: {img.source || "TBA"}</p>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Videos */}
        {tab === "videos" && (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
            {multimedia.videos.map((vid, i) => (
              <div
                key={i}
                className="bg-white/20 backdrop-blur-md p-4 rounded-2xl shadow-lg hover:shadow-2xl hover:scale-105 transition-transform duration-300"
              >
                <h3 className="font-semibold text-white mb-2">{vid.title}</h3>
                <video src={vid.src} controls className="w-full h-64 rounded-lg shadow-inner">
                  Your browser does not support video playback.
                </video>
                <p className="text-gray-300 text-xs mt-2">Source: {vid.source}</p>
              </div>
            ))}
          </div>
        )}

        {/* Audio */}
        {tab === "audio" && (
          <div className="space-y-6 mt-6">
            {multimedia.audio.map((a, i) => (
              <div
                key={i}
                className="bg-white/20 backdrop-blur-md p-6 rounded-2xl shadow-lg hover:shadow-2xl hover:scale-105 transition-transform duration-300"
              >
                <h3 className="font-semibold text-white mb-2">{a.title}</h3>
                <audio controls className="w-full rounded-md">
                  <source src={a.src} type={a.type || "audio/mpeg"} />
                  Your browser does not support audio playback.
                </audio>
                <p className="text-gray-300 text-xs mt-2">Source: {a.source}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

/* Tab Button Component */
function TabButton({ active, children, onClick }) {
  return (
    <button
      onClick={onClick}
      className={`px-5 py-2 rounded-xl font-semibold transition-colors duration-300 ${
        active
          ? "bg-green-600 text-white shadow-lg"
          : "bg-white/20 text-white hover:bg-white/30"
      }`}
    >
      {children}
    </button>
  );
}
